import { useState, useEffect } from "react";
import Loader from "react-loader-spinner";
import LeaderboardTable from "../LeaderboardTable";

import {
  LeaderboardContainer,
  LoadingViewContainer,
  ErrorMessage,
} from "./styledComponents";

const apiStatusConstants = {
  initial: "INITIAL",
  inProgress: "IN_PROGRESS",
  success: "SUCCESS",
  failure: "FAILURE",
};

const Leaderboard = () => {
  const [apiResponse, setApiResponse] = useState({
    status: apiStatusConstants.initial,
    data: null,
    errorMsg: null,
  });

  useEffect(() => {
    const getLeaderboardData = async () => {
      setApiResponse({
        status: apiStatusConstants.inProgress,
        data: null,
        errorMsg: null,
      });

      const url = "https://apis.ccbp.in/leaderboard";
      const options = {
        method: "GET",
        headers: {
          Authorization:
            "Bearer yJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VybmFtZSI6InJhaHVsIiwicm9sZSI6IlBSSU1FX1VTRVIiLCJpYXQiOjE2MjMwNjU1MzJ9.D13s5wN3Oh59aa_qtXMo3Ec4wojOx0EZh8Xr5C5sRkU",
        },
      };
      const response = await fetch(url, options);
      const responseData = await response.json();
      console.log(responseData);
      const newData = responseData.leaderboard_data.map((each) => ({
        id: each.id,
        name: each.name,
        language: each.language,
        rank: each.rank,
        score: each.score,
        timeSpent: each.time_spent,
        profileImgUrl: each.profile_image_url,
      }));
      if (response.ok) {
        setApiResponse((prev) => ({
          ...prev,
          status: apiStatusConstants.success,
          data: newData,
        }));
      } else {
        setApiResponse((prev) => ({
          ...prev,
          status: apiStatusConstants.failure,
          errorMsg: responseData.error_msg,
        }));
      }
    };

    getLeaderboardData();
  }, []);

  const renderFailureView = () => {
    const { errorMsg } = apiResponse;
    return <ErrorMessage>{errorMsg}</ErrorMessage>;
  };

  const renderSuccessView = () => {
    const { data } = apiResponse;
    return <LeaderboardTable leaderboardData={data} />;
  };

  const renderLoadingView = () => (
    <LoadingViewContainer>
      <Loader type="ThreeDots" color="#ffffff" height="50" width="50" />
    </LoadingViewContainer>
  );

  const renderLeaderboard = () => {
    const { status } = apiResponse;

    switch (status) {
      case apiStatusConstants.inProgress:
        return renderLoadingView();
      case apiStatusConstants.success:
        return renderSuccessView();
      case apiStatusConstants.failure:
        return renderFailureView();
      default:
        return null;
    }
  };

  return <LeaderboardContainer>{renderLeaderboard()}</LeaderboardContainer>;
};

export default Leaderboard;
